import React, {Component, Fragment} from 'react';
import {
    Row, Col,
    Button,
    CardHeader,
    Container,
    Card,
    CardBody,
    CardFooter,
    DropdownItem, DropdownToggle, DropdownMenu,
    UncontrolledButtonDropdown
} from 'reactstrap';


import ReactTable from "react-table";


import Ionicon from 'react-ionicons';


export default class AnalyticsDashboard1 extends Component {


    componentDidMount() {
        fetch("https://jsonplaceholder.typicode.com/photos")
        .then(res => res.json())
        .then(parsedJSON => parsedJSON.map(data => (
        {
            album: data.albumId,
            lastName: data.id,
            age: data.title,
            status: data.url,
            gambar: data.thumbnailUrl
        }
        )))
        .then(items => this.setState({
            items,
            isLoaded: false
        }))
        .catch(error => alert('parsing failed', error))
    }

    render() {
        const {selectedOption} = this.state;
        const {data} = this.state;
        const { items } = this.state;
        const settings = {
            className: "",
            centerMode: false,
            infinite: true,
            slidesToShow: 1,
            speed: 500,
            dots: true,
        };

        return (
            <Fragment>
            <Container fluid>
            <Card className="mb-3">
            <CardHeader className="card-header-tab z-index-6">
            <div className="card-header-title font-size-lg text-capitalize font-weight-normal">
            <i className="header-icon lnr-charts icon-gradient bg-happy-green"> </i>
            Gambar Kegiatan
            </div>

            <div className="btn-actions-pane-right actions-icon-btn">
            <UncontrolledButtonDropdown>
            <DropdownToggle className="btn-icon btn-icon-only" color="link">
            <i className="pe-7s-menu btn-icon-wrapper"/>
            </DropdownToggle>
            <DropdownMenu
            className="dropdown-menu-right rm-pointers dropdown-menu-shadow dropdown-menu-hover-link">
            <DropdownItem header>Header</DropdownItem>
            <DropdownItem>
            <i className="dropdown-icon lnr-inbox"> </i>
            <span>Menus</span>
            </DropdownItem>
            <DropdownItem>
            <i className="dropdown-icon lnr-file-empty"> </i>
            <span>Settings</span>
            </DropdownItem>
            <DropdownItem>
            <i className="dropdown-icon lnr-book"> </i>
            <span>Actions</span>
            </DropdownItem>
            <DropdownItem divider/>
            <div className="p-3 text-right">
            <Button className="mr-2 btn-shadow btn-sm" color="link">View
            Details</Button>
            <Button className="mr-2 btn-shadow btn-sm"
            color="primary">Action</Button>
            </div>
            </DropdownMenu>
            </UncontrolledButtonDropdown>
            </div>
            </CardHeader>
            <CardBody>
            <ReactTable
            data={items}
            columns={[
                {
                    Header: "Name",
                    columns: [
                    {
                        Header: "First Name",
                        accessor: "album"
                    },
                    {
                        Header: "Last Name",
                        accessor: "lastName"
                    }
                    ]
                },
                {
                    Header: "Info",
                    columns: [
                    {
                        Header: "Age",
                        accessor: "age"
                    },
                    {
                        Header: "Status",
                        accessor: "status"
                    }
                    ]
                },
                {
                    Header: 'image',
                    columns: [
                    {
                        Header: "Image",
                        id: "gambar",
                         accessor: data => (<img src={data.gambar} />)
                    }
                    ]
                },
                {
                    Header: 'aksi',
                    columns: [

                    {
                        Header: 'Actions',
                        accessor: 'actions',
                        Cell: row => (
                        <div className="d-block w-100 text-center">
                        <Button outline className="mb-2 mr-2 btn-dashed btn-shadow-info active" color="info"><i className="pe-7s-look"></i>
                        
                        </Button>
                        <Button outline className="mb-2 mr-2 btn-dashed btn-shadow-primary active" color="primary" ><i className="pe-7s-pen"></i></Button>
                        <Button outline className="mb-2 mr-2 btn-dashed btn-shadow-danger active" color="danger"><i className="pe-7s-trash"></i></Button>
                        </div>
                        )
                    }
                    ]
                }
                ]}
                defaultPageSize={20}
                style={{
                    height: "428px" // This will force the table body to overflow and scroll, since there is not enough room
                }}
                className="-striped -highlight -fixed"
                />
                </CardBody> 
                <CardFooter className="text-center d-block p-3">
                <Button color="primary" className="btn-pill btn-shadow btn-wide fsize-1" size="lg">
                <span className="mr-2 opacity-7">
                <Ionicon color="#ffffff" icon="ios-analytics-outline" beat={true}/>
                </span>
                <span className="mr-1">
                View Complete Report
                </span>
                </Button>
                </CardFooter>
                </Card>
</Container>
</Fragment>                                                            
)
}
}
