import React, {Component, Fragment} from 'react';
import {Col,Button,CardHeader,Card,CardBody,Input,Modal,ModalHeader,ModalBody,ModalFooter,Form,FormGroup,Label,} from "reactstrap";
import ReactTable from "react-table";
import { ResponsiveContainer } from 'recharts';

export default class IncomeReport extends Component {

    constructor(props) {
        super(props);
        this.state = {
          modal: false,
          items: [],
          editdata: null,
          edit: false
        };
        
        this.tguru = this.tguru.bind(this);
        this.editguru = this.editguru.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.dataedit = this.dataedit.bind(this);
    }
    
    componentDidMount() {
      fetch("https://jsonplaceholder.typicode.com/users")
        .then(res => res.json())
        .then(parsedJSON => parsedJSON.map(data => (
            {
                id: data.id,
                name: data.name,
                username: data.username,
                email: data.email,
            addres4: data.phone
               
            }
            )))
            .then(items => this.setState({
                items,
                isLoaded: false
            }))
            .catch(error => alert('gagal mengambil data API', error))
        }
        
        tguru() {
            this.setState({
                modal: !this.state.modal
            });
        }
        
        editguru(items) {
            this.setState({
                editdata: items.original,
                edit: !this.state.edit
            });
        }
        
        handleChange(id, value) {
            var d = Object.assign({}, this.state.editdata)
            Object.assign(d, { [id]: value })
            this.setState({
                editdata: d
            });
            setTimeout(() => {
            console.log(this.state)
        }, 1500)
    }

    deletedata(id){
      // console.log(id);

      const { items } = this.state;
       this.setState({
        items:items.filter(item => item.id !== id)
      })

    }

    tambahguru(){
      fetch('https://jsonplaceholder.typicode.com/users', {
        method: 'POST',
        body: JSON.stringify({
          albumId: this.state.editdata.id,
          title: this.state.editdata.name,
          status: this.state.editdata.status
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8"
        }
      })
        .then(response => response.json())
        .then(json => console.log(json))
    }

  dataedit(id,value){
    fetch('https://jsonplaceholder.typicode.com/users', {
      method: 'post',
      body: JSON.stringify({
        id: this.state.editdata.id,
        name: this.state.editdata.name,
        email: this.state.editdata.email
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8"
      }
    })
      .then(response =>{ 
        let postId = response.data.id;

    const items = [...this.state.items];

    const postEdit = items.findIndex(item => postId === item.id)

    items[postEdit] = value;
    this.setState({
      items
    })}
        )
      .then(json => console.log(json))

    }

render() {

    const { items } = this.state;

   return (

     <Fragment>

{/* MODAL */}

{/* Modal tambah Guru */}

           <Modal isOpen={this.state.modal} toggle={this.tguru} className={this.props.className}>
           <Form>
               <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>

               <ModalBody>
                  
             <FormGroup>
               <Label for="exampleEmail" sm={4}>ID</Label>
               <Col sm={8}>
                 <Input type="text" id="id" value={this.state.editdata ? this.state.editdata.id : ""}
                   onChange={(d) => this.handleChange('id', d.target.value)}
                   name="album" />
               </Col>
             </FormGroup>

             <FormGroup>
               <Label for="exampleEmail" sm={4}>ID Album</Label>
