import React,{ Component } from 'react';
import Awal from './awal';
import './squarec.css';

class Board extends Component {

  oke(i) {
    return <Awal value={i} />;
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.oke(0)}
          {this.oke(1)}
          {this.oke(2)}
        </div>
        <div className="board-row">
          {this.oke(3)}
          {this.oke(4)}
          {this.oke(5)}
        </div>
        <div className="board-row">
          {this.oke(6)}
          {this.oke(7)}
          {this.oke(8)}
        </div>
      </div>
    );
  }
}

export default Board;
