import React, {Component, Fragment} from 'react';

import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

import PageTitleAlt3 from '../../../Layout/AppMain/PageTitleAlt3';

import Chart from 'react-apexcharts'

import IncomeReport from '../Commerce/Examples/Components/IncomeReport';
import IncomeReport2 from '../Commerce/Examples/Components/IncomeReport2';

import bg1 from '../../../assets/utils/images/dropdown-header/abstract1.jpg';

import avatar1 from '../../../assets/utils/images/avatars/1.jpg';
import avatar2 from '../../../assets/utils/images/avatars/2.jpg';
import avatar3 from '../../../assets/utils/images/avatars/3.jpg';
import avatar4 from '../../../assets/utils/images/avatars/4.jpg';

import classnames from 'classnames';

import {
    Row, Col,
    Button,
    CardHeader,
    Table,
    ButtonGroup,
    Nav,
    NavItem,
    NavLink,
    TabContent,
    TabPane,
    Popover,
    PopoverBody,
    Progress,
    Card,
    CardBody,
    CardFooter,
    CardTitle, 
    Container,
    ListGroup,
    ListGroupItem,
    CustomInput, Input,
    Dropdown, DropdownItem, DropdownToggle, DropdownMenu,
    UncontrolledButtonDropdown,
    Modal, ModalHeader, ModalBody, ModalFooter

} from 'reactstrap';


import ReactTable from "react-table";

import {
    ResponsiveContainer,
    BarChart,
    Bar,
    LineChart,
    Tooltip,
    Line
} from 'recharts';

import CountUp from 'react-countup';

import {
    faAngleUp,
    faAngleDown,
    faCommentDots,
    faBullhorn,
    faBusinessTime,
    faCog
} from '@fortawesome/free-solid-svg-icons';

import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';

export default class MinimalDashboard2 extends Component {

    constructor(props) {
        super(props);


        this.state = {
          modal: false
      };

      this.tambah = this.tambah.bind(this);

      this.toggle = this.toggle.bind(this);

      this.togglePop1 = this.togglePop1.bind(this);

      this.state = {
        popoverOpen1: false,
        activeTab: '2',

        optionsMixedChart: {
            chart: {
                height: 350,
                type: 'line',
                stacked: false,
            },
            stroke: {
                width: [0, 2, 5],
                curve: 'smooth'
            },

            fill: {
                opacity: [0.85,0.25,1],
                gradient: {
                    inverseColors: false,
                    shade: 'light',
                    type: "vertical",
                    opacityFrom: 0.85,
                    opacityTo: 0.55,
                    stops: [0, 100, 100, 100]
                }
            },
            labels: ['01/01/2003', '02/01/2003','03/01/2003','04/01/2003','05/01/2003','06/01/2003','07/01/2003','08/01/2003','09/01/2003','10/01/2003','11/01/2003'],
            markers: {
                size: 0
            },
            xaxis: {
                type:'datetime'
            },
            yaxis: {
                title: {
                    text: 'Points',
                },
                min: 0
            },
            tooltip: {
                shared: true,
                intersect: false,
                y: {
                    formatter: function (y) {
                        if(typeof y !== "undefined") {
                            return  y.toFixed(0) + " points";
                        }
                        return y;

                    }
                }
            }

        },
        seriesMixedChart: [{
            name: 'TEAM A',
            type: 'column',
            data: [23, 11, 22, 27, 13, 22, 37, 21, 44, 22, 30]
        }, {
            name: 'TEAM B',
            type: 'bar',
            data: [44, 55, 41, 67, 22, 43, 21, 41, 56, 27, 43]
        }, {
            name: 'TEAM C',
            type: 'line',
            data: [30, 25, 36, 30, 45, 35, 64, 52, 59, 36, 39]
        }],
    }
}

togglePop1() {
    this.setState({
        popoverOpen1: !this.state.popoverOpen1
    });
}

toggle(tab) {
    if (this.state.activeTab !== tab) {
        this.setState({
            activeTab: tab
        });
    }
}

tambah() {
    this.setState({
      modal: !this.state.modal
  });
}

render() {
    const closeBtn = <button className="close" onClick={this.tambah}>&times;</button>;

    return (
    <Fragment>
    <ReactCSSTransitionGroup
    component="div"
    transitionName="TabsAnimation"
    transitionAppear={true}
    transitionAppearTimeout={0}
    transitionEnter={false}
    transitionLeave={false}>
    <PageTitleAlt3
    heading="Container Dashboard"
    subheading="This is an example dashboard created using build-in elements and components."
    icon="lnr-apartment icon-gradient bg-mean-fruit"
    />
    <Card tabs="true" className="mb-3">
    <CardHeader className="tabs-lg-alternate">
    <Nav justified>
    <NavItem>
    <NavLink href="javascript:void(0);"
    className={classnames({active: this.state.activeTab === '1'})}
    onClick={() => {
     this.toggle('1');
 }}
 >
 <h1>Data Guru</h1>
 <div className="tab-subheading">
 <span className="pr-2 opacity-6">
 <FontAwesomeIcon icon={faCommentDots}/>
 </span>
 Totals
 </div>
 </NavLink>
 </NavItem>
 <NavItem>
 <NavLink href="javascript:void(0);"
 className={classnames({active: this.state.activeTab === '2'})}
 onClick={() => {
     this.toggle('2');
 }}
 >
 <h1>Data Siswa</h1>
 <div className="tab-subheading">
 Products
 </div>
 </NavLink>
 </NavItem>

 <NavItem>
 <NavLink href="javascript:void(0);"
 className={classnames({active: this.state.activeTab === '3'})}
 onClick={() => {
     this.toggle('3');
 }}
 >
