import React,{ Component } from 'react';
import Awal from './awal';
import './squarec.css';




class Board extends Component {
  constructor(props){
      super(props);
      this.state = {
        history: [{
          okes: Array(9).fill(null),
        }],
        xIsNext: true,
      };
  }

  handleClick(i) {
    const history = this.state.history;
    const current = history[history.length - 1];
    const okes = current.okes.slice();

    if (calculateWinner(okes) || okes[i]) {
      return;
    }

    okes[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({ 
      history: history.concat([{
        okes: okes,
      }]),
      xIsNext: !this.state.xIsNext,
    });
  }

  oke(i) {
    return <Awal value={this.state.okes[i]}
     onClick={() =>this.props.onClick(i)} />;
  }

  render() {


    return (
      <div>

        <div className="board-row">
          {this.oke(0)}
          {this.oke(1)}
          {this.oke(2)}
        </div>
        <div className="board-row">
          {this.oke(3)}
          {this.oke(4)}
          {this.oke(5)}
        </div>
        <div className="board-row">
          {this.oke(6)}
          {this.oke(7)}
          {this.oke(8)}
        </div>
      </div>
    );
  }
}

function calculateWinner(okes) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (okes[a] && okes[a] === okes[b] && okes[a] === okes[c]) {
      return okes[a];
    }
  }
  return null;
}

export default Board;
