import React, { Component } from 'react';
import './squarec.css';

class awal extends Component {

    constructor(props){
        super(props);
        this.state = {
            value : null, //state isi yang kosong
        };
    }

    render() {
        return (
            <button className="square" onClick={() => this.setState({value:'X'})} >
                {this.state.value}
            </button>
        );
    }
}

export default awal;
